// post/[id]
// [id].tsx [id]는 바뀔 자리 (다이나믹 라우팅)

import axios from 'axios';
import { useRouter } from 'next/router';
import { useSelector } from 'react-redux';
import { END } from 'redux-saga';
import AppLayout from '../../components/AppLayout';
import PostCard from '../../components/PostCard';
import { LOAD_POST_REQUEST } from '../../reducers/post';
import { LOAD_MY_INFO_REQUEST } from '../../reducers/user';
import wrapper from '../../store/configureStore';

const Post = () => {
    const router = useRouter();
    // 뒤에 숫자 바뀌도록 구조분해 동적 라우팅
    const { id } = router.query;
    // 좀 더 정확히 알고싶으면 SQL Like query 검색

    const { singlePost } = useSelector((state: any) => state.post);

    return (
        <AppLayout>
            <PostCard post={singlePost} />
        </AppLayout>
    );
};

// export async function getStaticPaths() {
//   return {
//     paths: [
//       { params: { id: '1' } },
//       { params: { id: '2' } },
//       { params: { id: '3' } },
//     ],
//     fallback: true,
//   };
// }

export const getServerSideProps = wrapper.getServerSideProps((store): any => async ({ req }) => {
    console.log(store, req)
    const cookie = req ? req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (req && cookie) {
        axios.defaults.headers.Cookie = cookie;
    }
    store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    store.dispatch({
        type: LOAD_POST_REQUEST,
        data: 2
    });
    store.dispatch(END);
    await store.sagaTask.toPromise();
})

export default Post;
