// Next.js는 import React from 'react' 구문이 필요없음
import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import AppLayout from "../components/AppLayout";
import PostCard from '../components/PostCard';
import PostForm from '../components/PostForm';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state: any) => state.user);
  const { mainPosts, hasMorePost, loadPostsLoading } = useSelector((state: any) => state.post);
  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      if (window.scrollY + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
        if (hasMorePost && !loadPostsLoading) {
          dispatch({
            type: LOAD_POSTS_REQUEST,
            data: mainPosts[mainPosts.length - 1].id,
          });
        }
      }
    }
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [mainPosts, hasMorePost, loadPostsLoading]);

  return (
    // 다른 컴포넌트로 감싸줄 경우 다른 컴포넌트 사용 가능
    <AppLayout>
      {me && <PostForm />}
      {mainPosts.map((post: any) => <PostCard key={post.id} post={post} />)}
    </AppLayout>
  );
};

export default Home;
