interface IUser {
    isLoggedIn: boolean,
    user: string
}


const initialState = {
    user: {

    },
    post: {

    }
}



// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case HYDRATE:
            return { ...state, ...action.payload };
        default:
            return state;
    }
}

export default rootReducer;
