import { EllipsisOutlined, HeartOutlined, MessageOutlined, RetweetOutlined } from '@ant-design/icons';
import { Avatar, Button, Card, Popover } from 'antd';
import { useSelector } from 'react-redux';
import PostImages from './PostImages';

interface Me {
    id: number,
    User: object,
    content: string,
    createdAt: object,
    Comments: object[]
    Images: object[]
}

const PostCard = ({ post }) => {
    const { me } = useSelector((state: any) => state.user.me?.id)
    // state.user.me && state.user.me.id 를 옵셔널 체이닝으로 줄여서
    const id = me?.id;
    return (
        <div>
            {/* cover, actions는 antd기능 */}
            {/* 배열 안에 jsx를 넣을 땐 key */}
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    // liked
                    //     ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onToggleLike} />
                    //     : <HeartOutlined key="heart" onClick={onToggleLike} />,
                    <MessageOutlined key="message" />,
                    // onClick={onToggleComment}
                    <Popover
                        key="ellipsis"
                        content={(
                            <Button.Group>
                                {id && post.UserId === id
                                    ? (
                                        <>
                                            <Button>수정</Button>
                                            <Button type="danger">삭제</Button>
                                            {/* loading={removePostLoading} onClick={onRemovePost} */}
                                        </>
                                    )
                                    : <Button>신고</Button>}
                            </Button.Group>
                        )}
                    >
                        <EllipsisOutlined />
                    </Popover>,
                ]}
            // extra={<FollowButton post={post} />}
            >
                <Card.Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={post.content}
                // <PostCardContent postData={post.content} />
                />
            </Card>
            {/* <CommentForm />
            <Comments /> */}
        </div >
    )
}

export default PostCard;
